//setup instafeed and run slick after images have loaded
  var feed = new Instafeed({
    //access token generated by this thing - http://instagram.pixelunion.net/
    accessToken: '7233319241.1677ed0.5c0b29a207b74fa5bdbe2f4206426a54',
    get: 'user',
    userId: '7233319241',
    limit: 20,
    resolution: 'low_resolution',
    template: '<div class="instaThumb"><a href="{{link}}" target="_blank"><img data-lazy="      {{image}}" alt="{{caption}}" /></a></div>',
    after: function(){
      $('#instafeed').slick({
      arrows: false,
      autoplay: true,
      autoplaySpeed: 5000,
      speed: 1000,
      slidesToShow: 4,
      slidesToScroll: 1,
      responsive: [
      
    {
      breakpoint: 768,
      settings: {
        slidesToShow: 3, slidesToScroll: 3
      }
    },
    {
      breakpoint: 480,
      settings: {
        slidesToShow: 2, slidesToScroll: 2
      }
    }
  ]
      });
    }
  });
  feed.run();